import React, {useEffect, useState} from 'react';
import axios from 'axios';
import FitnessClassCard from './FitnessClassCard';
import { useNavigate } from 'react-router-dom';

// still needs search bar


const Class = () => {

    const initialClientState = {
        username: '',
        password: '', 
        role: 'client'
    }
    const initialInstructorState = {
        username: '',
        password: '', 
        role: 'instructor'
    }

    const [creds, setCreds] = useState(initialClientState);
    const [instCreds, setInstCreds] = useState(initialInstructorState);
    const navigate = useNavigate();

     const [classes, setClasses] = useState();

    useEffect(() => {

        axios.post(`https://anywhere-fitness-buildweek.herokuapp.com/api/clients/login`, creds)
            .then(resp => {
                console.log('client:', resp)
                const { token } = resp.data;
                localStorage.setItem('token', token);
                navigate('/classes')
            })
            .catch(err => {
                console.log(err)
            })
        const getClass = () => {
            axios.get(`https://anywhere-fitness-buildweek.herokuapp.com/api/clients/classes`)
            .then(res => {
                console.log(res);
                 setClasses(res);
            }).catch(err => {
                console.error(err);
            })
        }
        getClass();
    }, []);

    const handleChanges = (e) => {
        e.preventDefault();
        setClasses({
            ...classes, 
        [e.target.value]: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        /* needs to plug search criteria into url */
        axios.get(`https://anywhere-fitness-buildweek.herokuapp.com/api/clients/classes`)
            .then((res) => {
                console.log(res)
                 setClasses({
                     ...classes,
                     classes: res
                })
            }).catch((err) => {
                console.error(err)
            })
    };

    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input onChange={handleChanges}/>
            </form>
            <div>
            {/* {
                classes.map(i=><FitnessClassCard key={i.id} />)
            } */}
            </div> 
        </div>
    )
}

export default Class;
