import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

import axios from 'axios';
import ClassCard from './FitnessClassCard';
import './Class.css';

import axiosWithAuth from '../utils/axiosWithAuth';

// still needs search bar

const classCard = (props) => {
    const fitClass = props.fitClass;
    return(
        <tr>
            <td>{fitClass.class_name}</td>
            <td>{fitClass.class_type}</td>
            <td>{fitClass.class_intensity_level}</td>
            <td>{fitClass.class_location}</td>
            <td>{fitClass.class_start_time}</td>
            <td>{fitClass.class_duration}</td>
            <td>{fitClass.max_class_size}</td>
            <button onClick={() => props.handleEdit(fitClass.class_id)}>Edit</button>
            <button onClick={() => props.handleDelete(fitClass.class_id)}>Delete</button>
        </tr>
    )
};



const Class = () => {

     const [classes, setClasses] = useState();
     const { id } = useParams();


    useEffect(() => {
        const getClass = () => {
            axiosWithAuth().get(`/clients/classes`)
            .then(res => {
                console.log('hello from get class',res);
                setClasses(res.data);
            }).catch(err => {
                console.error(err);
            });
            }
                getClass();
            }, []);

    const handleChanges = (e) => {
        e.preventDefault();
        setClasses({
            ...classes, 
        [e.target.value]: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        /* needs to plug search criteria into url */
        axiosWithAuth().get(`/clients/classes/:${id}`)
            .then(res => {
                console.log('hello from submit', res);
                setClasses(res.data);
            }).catch(err => {
                console.error(err);
            })
    };

    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input onChange={handleChanges}/>
                <button>Search</button>
            </form>
            <div>
            {
                classes.map(i => classCard(i))
            }
            </div> 
        </div>
    )
}

export default Class;
