import dummyData from './DummyData';

import axiosWithAuth from '../utils/axiosWithAuth';

// still needs search bar


const Class = (props) => {

     const [classes, setClasses] = useState();
     const { id } = useParams();


    useEffect(() => {
            axiosWithAuth().get(`/clients/classes`)
