        //         setClasses(res.data);
        //     }).catch(err => {
        //         console.error(err);
        //     })
    };

    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input onChange={handleChanges}/>
                <button>Search</button>
            </form>
            <div>
            {/* {
                 classes && classes.map(i => <ClassCard />)
            } */}
            </div> 
        </div>
    )
}

export default Class;
